function OnUpdate(doc,meta) {
   var src_doc= doc;
   if(src_doc["update_field"]==null){
    src_doc["update_field"]=1;
   }
   else{
    src_doc["update_field"]=src_doc["update_field"]+1;
   }    
   src_doc[meta.id]=src_doc;
}

function OnDelete(meta){
    src_doc[meta.id]="doc deleted";

}
